Properties.Octopus.Action.Script.ScriptBody:
param(
    [string]$OpeningStepName = "",
    [string]$Subdomain = "",
    [string]$Token = ""
) 

function Get-Param($Name, [switch]$Required, $Default) {
    $Result = $null

    if ($OctopusParameters -ne $null) {
        $Result = $OctopusParameters[$Name]
    }

    if ($Result -eq $null) {
        $variable = Get-Variable $Name -EA SilentlyContinue   
        if ($variable -ne $null) {
            $Result = $variable.Value
        }
    }

    if ($Result -eq $null -or $Result -eq "") {
        if ($Required) {
            throw "Missing parameter value $Name"
        } else {
            $Result = $Default
        }
    }

    return $Result
}

& {
    param([string]$OpeningStepName, [string]$Subdomain, [string]$Token)

	$WindowId = $OctopusParameters["Octopus.Action[$OpeningStepName].Output.WindowId"]

	try {
		Invoke-RestMethod -Uri ("https://$Subdomain.pagerduty.com/api/v1/maintenance_windows/$WindowId") -method Delete -ContentType "application/json" -Headers @{"Authorization"=("Token token=$Token")}
		Write-Host "PagerDuty window closed"
	} catch [System.Exception] {
        Write-Host $_.Exception.Message

        $ResponseStream = $_.Exception.Response.GetResponseStream()
        $Reader = New-Object System.IO.StreamReader($ResponseStream)
        $Reader.ReadToEnd() | Write-Host

		Exit 0
    }
} (Get-Param 'OpeningStepName' -Required) (Get-Param 'Subdomain' -Required) (Get-Param 'Token' -Required)
