Properties.Octopus.Action.Script.ScriptBody:
param(
    [array]$ServiceIds = @(""),
    [string]$RequesterId = "",
    [string]$Description = "",
    [int]$Minutes = 10,
    [string]$Subdomain = "",
    [string]$Token = ""
) 

$ErrorActionPreference = "Stop" 

function Get-Param($Name, [switch]$Required, $Default) {
    $Result = $null

    if ($OctopusParameters -ne $null) {
        $Result = $OctopusParameters[$Name]
    }

    if ($Result -eq $null) {
        $variable = Get-Variable $Name -EA SilentlyContinue   
        if ($variable -ne $null) {
            $Result = $variable.Value
        }
    }

    if ($Result -eq $null -or $Result -eq "") {
        if ($Required) {
            throw "Missing parameter value $Name"
        } else {
            $Result = $Default
        }
    }

    return $Result
}

& {
    param([array]$ServiceIds, [string]$RequesterId, [string]$Description, [int]$Minutes, [string]$Subdomain, [string]$Token)

    Write-Host "Opening PagerDuty window for $Description"

    try {
        $ArrayOfServices = $ServiceIds.split(",") | foreach { $_.trim() }
        $Start = ((Get-Date)).ToString("yyyy-MM-ddTHH:mm:sszzzzZ");
        $End = ((Get-Date).AddMinutes($Minutes)).ToString("yyyy-MM-ddTHH:mm:sszzzzZ");

        Write-Host "Window will be open from $Start -> $End"

        $Post = @{
            'maintenance_window'= @{
                'start_time' = $Start
                'end_time' = $End
                'description' = $Description
                'service_ids' = @($ArrayOfServices)
            }
            'requester_id' = $RequesterId
        } | ConvertTo-Json 

        $ResponseObj = Invoke-RestMethod -Uri ("https://$Subdomain.pagerduty.com/api/v1/maintenance_windows") -method Post -Body $Post -ContentType "application/json" -Headers @{"Authorization"=("Token token=$Token")}
        $WindowId = $ResponseObj.maintenance_window.id

        Write-Host "Window Id $WindowId created"

        if(Get-Command -name "Set-OctopusVariable" -ErrorAction SilentlyContinue) {
            Set-OctopusVariable -name "WindowId" -value $WindowId
        } else {
            Write-Host "Octopus output variable not set"
        }
    } catch [System.Exception] {
        Write-Host "Error while opening PagerDuty window"
        Write-Host $_.Exception.Message

        $ResponseStream = $_.Exception.Response.GetResponseStream()
        $Reader = New-Object System.IO.StreamReader($ResponseStream)
        $Reader.ReadToEnd() | Write-Host

        Exit 1
    }
} (Get-Param 'ServiceIds' -Required) (Get-Param 'RequesterId' -Required) (Get-Param 'Description' -Required) (Get-Param 'Minutes' -Required) (Get-Param 'Subdomain' -Required) (Get-Param 'Token' -Required)
