Properties.Octopus.Action.Script.ScriptBody:
Function Get-Parameter() {
    Param(
        [parameter(Mandatory=$true)]
        [string]$Name, 
        [switch]$Required, 
        $Default, 
        [switch]$FailOnValidate
    )

    $result = $null
    $errMessage = [string]::Empty

    If ($OctopusParameters -ne $null) {
        $result = $OctopusParameters[$Name]
        Write-Host ("Octopus parameter value for " + $Name + ": " + $result)
    }

    If ($result -eq $null) {
        $variable = Get-Variable $Name -EA SilentlyContinue
        if ($variable -ne $null) {
            $result = $variable.Value
        }
    }

    If ($result -eq $null -or [string]::IsNullOrEmpty($result)) {
        If ($Required) {
            $errMessage = "Missing value for $Name"
        } ElseIf (-Not $Default -eq $null) {
            Write-Host ("Default value: " + $Default)
            $result = $Default
        }
    }

    If (-Not [string]::IsNullOrEmpty($errMessage)) {
        If ($FailOnValidate) {
            Throw $errMessage
        } Else {
            Write-Warning $errMessage
        }
    }

    return $result
}

Function Test-Any() { 
   begin { 
      $any = $false
   } 
   process { 
      $any = $true
   } 
   end { 
      $any
   } 
} 

& {
    Write-Host "Start PingdomCreateUptimeCheck"

    Add-Type -AssemblyName System.Web

    $pingdomUsername = [string] (Get-Parameter -Name "Pingdom.Username" -Required -FailOnValidate)
    $pingdomPassword = [string] (Get-Parameter -Name "Pingdom.Password" -Required -FailOnValidate)
    $pingdomAppKey = [string] (Get-Parameter -Name "Pingdom.AppKey" -Required -FailOnValidate)

    $pingdomCheckName = [string] (Get-Parameter -Name "Pingdom.CheckName" -Required -FailOnValidate)
    $pingdomCheckTarget = [string] (Get-Parameter -Name "Pingdom.CheckTarget" -Required -FailOnValidate)
    $pingdomCheckType = "http"
    $pingdomCheckIntervalMinutes = [System.Nullable[int]] (Get-Parameter -Name "Pingdom.CheckIntervalMinutes" -FailOnValidate)
    $pingdomCheckContactIds = [string] (Get-Parameter -Name "Pingdom.CheckContactIds" -FailOnValidate)
    $pingdomCheckSendNotificationWhenDown = [System.Nullable[int]] (Get-Parameter -Name "Pingdom.CheckSendNotificationWhenDown" -FailOnValidate)
    $pingdomCheckNotifyAgainEvery = [System.Nullable[int]] (Get-Parameter -Name "Pingdom.CheckNotifyAgainEvery" -FailOnValidate)
    $pingdomCheckSendToEmail = [System.Convert]::ToBoolean([string](Get-Parameter -Name "Pingdom.CheckSendToEmail" -Default "True" -FailOnValidate))
    $pingdomCheckNotifyWhenBackUp = [System.Convert]::ToBoolean([string](Get-Parameter -Name "Pingdom.CheckNotifyWhenBackUp" -Default "True" -FailOnValidate))
    $pingdomCheckTags = [string] (Get-Parameter -Name "Pingdom.CheckTags" -FailOnValidate)
    $pingdomCheckHttpUrl = [string] (Get-Parameter -Name "Pingdom.CheckHttpUrl" -FailOnValidate)
    $pingdomCheckHttpEncryptionEnabled = [System.Convert]::ToBoolean([string](Get-Parameter -Name "Pingdom.CheckHttpEncryptionEnabled" -Default "False" -FailOnValidate))
    $pingdomCheckHttpTargetPort = [System.Nullable[int]] (Get-Parameter -Name "Pingdom.CheckHttpTargetPort" -FailOnValidate)
    $pingdomCheckAuth = [string] (Get-Parameter -Name "Pingdom.CheckAuth" -FailOnValidate)
    $pingdomCheckShouldContain = [string] (Get-Parameter -Name "Pingdom.CheckShouldContain" -FailOnValidate)
    $pingdomCheckShouldNotContain = [string] (Get-Parameter -Name "Pingdom.CheckShouldNotContain" -FailOnValidate)
    $pingdomCheckPostData = [string] (Get-Parameter -Name "Pingdom.CheckPostData" -FailOnValidate)

    $apiVersion = "2.0"
    $url = "https://api.pingdom.com/api/{0}/checks" -f $apiVersion
    $securePassword = ConvertTo-SecureString $pingdomPassword -AsPlainText -Force
    $credential = New-Object System.Management.Automation.PSCredential($pingdomUsername, $securePassword)
    $headers = @{ 
        "App-Key" = $pingdomAppKey
    }

    # Validate if check already registered

    Write-Host "Getting uptime check list to find check by name: $url"
    Try {
        $response = Invoke-RestMethod -Uri $url -Method Get -ContentType "application/json" -Credential $credential -Headers $headers
    } Catch {
        Write-Host "Error occured when getting uptime check list in Pingdom: " $_
    }

    $checkExists = $response.checks | Where-Object { $_.name -eq $pingdomCheckName } | Test-Any
    If ($checkExists -eq $true) {
        Write-Host "Check with name $pingdomCheckName already registered. New check will not be created!"
        Exit
    }

    # Create new check

    $apiParameters = @{}
    $apiParameters.Add("name", $pingdomCheckName)
    $apiParameters.Add("host", $pingdomCheckTarget)
    $apiParameters.Add("type", $pingdomCheckType)
    If ($pingdomCheckIntervalMinutes -ne $null) {
        $apiParameters.Add("resolution", $pingdomCheckIntervalMinutes)
    }
    If (-Not [string]::IsNullOrEmpty($pingdomCheckContactIds)) {
        $apiParameters.Add("contactids", $pingdomCheckContactIds)
    }
    If ($pingdomCheckSendToEmail -ne $null) {
        $apiParameters.Add("sendtoemail", $pingdomCheckSendToEmail)
    }
    If ($pingdomCheckSendNotificationWhenDown -ne $null) {
        $apiParameters.Add("sendnotificationwhendown", $pingdomCheckSendNotificationWhenDown)
    }
    If ($pingdomCheckNotifyAgainEvery -ne $null) {
        $apiParameters.Add("notifyagainevery", $pingdomCheckNotifyAgainEvery)
    }
    If ($pingdomCheckNotifyWhenBackUp -ne $null) {
        $apiParameters.Add("notifywhenbackup", $pingdomCheckNotifyWhenBackUp)
    }
    If (-Not [string]::IsNullOrEmpty($pingdomCheckTags)) {
        $apiParameters.Add("tags", $pingdomCheckTags)
    }
    If (-Not [string]::IsNullOrEmpty($pingdomCheckHttpUrl)) {
        $apiParameters.Add("url", $pingdomCheckHttpUrl)
    }
    If ($pingdomCheckHttpEncryptionEnabled -ne $null) {
        $apiParameters.Add("encryption", $pingdomCheckHttpEncryptionEnabled)
    }
    If ($pingdomCheckHttpTargetPort -ne $null) {
        $apiParameters.Add("port", $pingdomCheckHttpTargetPort)
    }
    If (-Not [string]::IsNullOrEmpty($pingdomCheckAuth)) {
        $apiParameters.Add("auth", $pingdomCheckAuth)
    }
    If (-Not [string]::IsNullOrEmpty($pingdomCheckShouldContain)) {
        $apiParameters.Add("shouldcontain", $pingdomCheckShouldContain)
    }
    If (-Not [string]::IsNullOrEmpty($pingdomCheckShouldNotContain)) {
        $apiParameters.Add("shouldnotcontain", $pingdomCheckShouldNotContain)
    }
    If (-Not [string]::IsNullOrEmpty($pingdomCheckPostData)) {
        $apiParameters.Add("postdata", $pingdomCheckPostData)
    }

    If ($apiParameters.Count -gt 0) {
        $queryString = ""
        $apiParameters.Keys | ForEach-Object { 
            $queryString += ($_ + "=" + [Web.HttpUtility]::HtmlEncode($apiParameters.Item($_)) + "&")  
        }
        $queryString = $queryString.Substring(0, $queryString.Length - 1)
        $url += "?$queryString"
    }

    Write-Host "Creating new uptime check: $url"
    Try {
        $response = Invoke-RestMethod -Uri $url -Method Post -ContentType "application/json" -Credential $credential -Headers $headers
        Write-Host "Successfully added uptime check in Pingdom: Name = " + ($response.check.name) + ", Id = " + ($response.check.id)
    } Catch {
        Write-Error "Error occured when adding uptime check in Pingdom: " $_
    }

    Write-Host "End PingdomCreateUptimeCheck"
}
